import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import './App.css';

import Question from './components/Question';
import Answer from './components/Answer';
import Statistics from './components/Statistics';
import questionsData from './data/questions.json';

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const navigate = useNavigate(); // Use the useNavigate hook for navigation

  useEffect(() => {
    setQuestions(questionsData);
  }, []);

  const handleAnswer = (isCorrect) => {
    if (isCorrect) {
      setCorrectAnswers(correctAnswers + 1);
    }

    // Check if this is the last question
    if (currentQuestionIndex < questions.length - 1) {
      // If not the last question, navigate to the Answer component
      navigate('/answer');
    } else {
      // If it's the last question, navigate to the Statistics component
      navigate('/statistics');
    }
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  return (
    <div className="App">
      <Routes>
        <Route
          path="/"
          element={
            currentQuestionIndex < questions.length ? (
              <Question
                question={questions[currentQuestionIndex]}
                onAnswer={handleAnswer}
              />
            ) : (
              <Statistics correctAnswers={correctAnswers} />
            )
          }
        />
        <Route
          path="/answer"
          element={
            currentQuestionIndex < questions.length - 1 ? (
              <Answer correct={true} text="Sample answer text" onNextQuestion={handleNextQuestion} />
            ) : (
              // Display the "Finish" button on the last question
              <Answer
                correct={true}
                text="Sample answer text"
                onFinish={() => navigate('/statistics')}
              />
            )
          }
        />
      </Routes>
    </div>
  );
}

export default App;
