import React, { useState } from 'react';
import './App.css';

import Question from './components/Question';
import Answer from './components/Answer';
import Statistics from './components/Statistics';
import questionsData from './data/questions.json';

const APP_STATES = {
  QUESTION: 'question',
  ANSWER: 'answer',
  FINISHED: 'finished',
};

function App() {
  const [currentState, setCurrentState] = useState(APP_STATES.QUESTION);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);

const handleAnswer = (isCorrect) => {
  if (isCorrect) {
    setCorrectAnswers(correctAnswers + 1);
  }

  if (currentQuestionIndex < questionsData.length - 1) {
    // Delay the transition to the next question for a moment
    setTimeout(() => {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setCurrentState(APP_STATES.QUESTION);
    }, 1000); // Adjust the delay duration as needed (e.g., 1000ms for 1 second)
  } else {
    setCurrentState(APP_STATES.FINISHED);
  }
};

const handleNextQuestion = () => {
  if (currentQuestionIndex < questionsData.length - 1) {
    setCurrentQuestionIndex(currentQuestionIndex + 1);
    setCurrentState(APP_STATES.QUESTION);
  } else {
    setCurrentState(APP_STATES.FINISHED);
  }
};

  return (
    <div className="App">
      {currentState === APP_STATES.QUESTION && (
        <Question
          question={questionsData[currentQuestionIndex]}
          onAnswer={handleAnswer}
        />
      )}
      {currentState === APP_STATES.ANSWER && (
        <Answer
          correct={true}
          text="Sample answer text"
          onNextQuestion={handleNextQuestion}
        />
      )}
      {currentState === APP_STATES.FINISHED && (
        <Statistics correctAnswers={correctAnswers} />
      )}
    </div>
  );
}

export default App;
