import React from 'react';

function Answer({ correct, onNextQuestion, isLastQuestion, question }) {
  const type = correct ? 'משרד' : 'מטרד';
  return (
    <div className={`answer-container-${correct ? 'correct' : 'wrong'}`}>
      <h1 className="answer-name">
        <a href={question.url} target="_blank">{question.name}</a> is {type}!
      </h1>
      <p>{question.text}</p>
      <a href={question.url} target="_blank" rel="noopener noreferrer">More Info</a>
      {isLastQuestion ? (
        <button onClick={onNextQuestion}>Finish</button>
      ) : (
        <button onClick={onNextQuestion}>Next Question</button>
      )}
    </div>
  );
}

export default Answer;
