import React, { useState, useEffect } from 'react';

function Question({ question, onAnswer }) {
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showAnswer, setShowAnswer] = useState(false);
  const isCorrect = selectedAnswer === question.type;

  useEffect(() => {
    setSelectedAnswer(null);
    setShowAnswer(false);
  }, [question]);

  const handleAnswerClick = (selectedType) => {
    setSelectedAnswer(selectedType);
    setShowAnswer(true);
    onAnswer(isCorrect);
  };

  return (
    <div className="question-container">
      <h2>{question.name}</h2>
      <p>{question.text}</p>
      <div className="answer-buttons">
        <button
          className={selectedAnswer === 0 ? 'selected' : ''}
          onClick={() => handleAnswerClick(0)}
        >
          Fruit
        </button>
        <button
          className={selectedAnswer === 1 ? 'selected' : ''}
          onClick={() => handleAnswerClick(1)}
        >
          Vegetable
        </button>
      </div>
      {showAnswer && (
        <div className={`answer ${isCorrect ? 'correct' : 'wrong'}`}>
          {isCorrect ? 'Correct' : 'Wrong'}
        </div>
      )}
    </div>
  );
}

export default Question;
